//const ENDPOINT = "http://localhost:8080";
//const ENDPOINT = "http://mockbin.org/bin/9bd025f3-b46c-4e90-80e6-6948a2d92e15?foo=bar&foo=baz"
const ENDPOINT = 'http://localhost:4003/db/login'

export default function LoginService({ username, password }) {
  console.log({username, password})
  return fetch(`${ENDPOINT}`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json"
    },
    body: JSON.stringify({username:username, password:password})
  })
    .then((res) => {

      if (!res.ok) throw new Error("Response is not ok");
      return res.json();
    })
    .then((res) => {
      const { token: jwt } = res;
      console.log('your jwt is : ',{jwt})
      return jwt;
    });
}
