    const [gifos, setGifs] = useState([])

    useEffect(()=>{

        const keywordTouse = keyword || localStorage.getItem('lastkeyword')

        getGifs({keywordTouse})
        .then((gifs) =>   {
            setLoading(true)


            setGifs(gifs)

        setLoading(false)
        localStorage.setItem('lastkeyword', keyword)
        })

    },[keyword])







