import { useState } from "react";
import { useLocation } from "wouter";

import useUser from "../hooks/useUser";
import Login from "./Login";
import Modal from "./modal";


export default  function  Fav({ id }) {
  const { isLogged, addfav, favs , is} = useUser();

  const [, navigate] = useLocation(false);

  const [showModal, setShowModal ] = useState()

const isFaved = favs.some(fav => {
 if(fav === id) return true 
 else{
  return fav.gif_id === id
 }
})



  let [label,emoji] = isFaved
  ? ["Remove Gif from fav", "❌"]
  : ["Add gif to favs", "❤️"];


  const HandleFav = () => {


    if (!isLogged) return setShowModal(true);

    addfav({ id });
  };


  const handleClose = () => {
    setShowModal(false)
  }

  return (
    <>
    <button onClick={HandleFav}>
      <span role={"img"} aria-label={label}>
        {emoji}
      </span>
    </button>
    {showModal && !isLogged ? <Modal onClose={handleClose}><Login/></Modal> : null}
    </>
  );
}
