import React, { useEffect, useReducer, useRef } from "react";


export default function useTitle({description , title})
{

    const prevTitle = useRef(document.title)

    const prevDescription = useRef(document.querySelector('meta[name-"description'))





    useEffect(()=>{
        const previousTitle = prevTitle.current
    document.title = `${title} | Giffy`

        return () => document.title = previousTitle
    }, [title])

    useEffect(()=>{

        const prevDesc = prevDescription.current

        const metaDescription = document.querySelector('meta[name-"description')
        if(description)
        {

            metaDescription.setAttribute('content', description)

            return () => metaDescription.setAttribute('content', description)
        }
    },description)

}
