    UPDATE_KEYWORD: 'update_keyword',
    UPDATE_RAITING: ' update_raiting'
  }



 const reducer  = (state, action) =>{
    switch(action.type){
      case ACTIONS.UPDATE_KEYWORD:
        return {
          ...state,
          keyword:  action.payload,
          time: state.times+1
        }
        case ACTIONS.UPDATE_RAITING:
          return{
            ...state,
