import React, { useEffect, useState, useRef } from "react";
import { Link } from "wouter";
import getTrending from "../pages/getTrendingService";
import { key } from "../settings/settings";

 function TrendingSearches(){


    const [trends, setTrends] = useState([])


    useEffect(()=>{

        getTrending()
        .then(gifs => {

            setTrends(gifs)
        })


    }
    ,[])




  const listItems =   trends.map(singleGif =>{
        return(
            <li key={singleGif.id}>
            <Link to ={`/search/${singleGif.title}` }>

            <small > {singleGif.title} </small>

            </Link>
            </li>
        )
    })

    return (

        <div>

            <ul > {listItems } </ul>



        </div>


    );

}


function usenearScreen({ref}){

    const [show , setShow] = useState(false)

    useEffect(()=>{
        const onChange =(entries, observer) =>{

            const element = entries[0]



            console.log(element.isIntersecting)


            if (element.isIntersecting)
            {
                setShow(true)
                observer.disconnect()
            }


        }

    },[])

}

export default function LazyTrending(){



const ref = useRef()

const [show] = use({ref})

return( <div ref={ref}>


{show ? <TrendingSearches/> : null}

</div>);

};
