import { useEffect, useState } from "react";
import React from "react";
import { useLocation } from "wouter";
import useUser from "../../hooks/useUser";
import "./Login.css"

export default function Login({onLogin}) {
  const [, navigate] = useLocation();

  const [username, setUserName] = useState("");

  const [password, setPassword] = useState("");

  const {  isLogingLoading    ,    login   ,    isLogged } = useUser();



  useEffect(() => {
    if (isLogged === true)
    { navigate("/")
     onLogin && onLogin()}

  }, [isLogged, navigate]);

  const OnSubmit = (event) => {
    event.preventDefault();
    login({username, password});
  };

  return (
    < div className="LoginPage">
    {(isLogingLoading)&& <h1 className="Success"> Checking credentials</h1>}


{!isLogingLoading&&
     ( 
     <form onSubmit={OnSubmit} >
      <h1 className="LoginTitle"> Login </h1>
        <label className="LabelForms" > Username  <input className="InputForm onPage"
          onChange={(e) => {
            setUserName(e.target.value);
          }}
          type="text"
          placeholder="username"
          value={username}
        />
        </label>

        <label className="LabelForms">password <input className="InputForm"
          onChange={(e) => {
            setPassword(e.target.value);
          }}
          type="password"
          placeholder="password"
          value={password}
        /></label>

        <button className="buttonForm"> Login </button>
      </form>
)}
    </div>
  );
}
