import { useReducer, } from "react";
import { useLocation } from "wouter";
import React from "react";
import {reducer} from "../hooks/useForm"
const RATINGS = ["g", "pg", "pg-13", "r"];




 function SearchForm({initialKeyword = '', initialRating='g'}) {



  //const [keyword, setKeyword] = useState(decodeURIComponent(initialKeyword));


  //const [times, setTimes] = useState(0)


  const useForm =({initialKeyword, initialRating}) =>{



     const [state , dispatch ]    =    useReducer(reducer, {
      keyword: decodeURIComponent(initialKeyword),
      rating: initialRating,
      times: 0,
     })

     const {keyword, rating, times} = state
     return{
      keyword,
      rating,
      times,
      update_keyword: keyword=>  dispatch({          type : ACTIONS.UPDATE_KEYWORD,           payload:  keyword   }),
      update_rating: (rating)=>   dispatch(   { type : ACTIONS.UPDATE_RAITING,       payload:  rating})

     }
    }



    const {keyword, rating, times, update_keyword, update_rating} = useForm({initialKeyword, initialRating})


     const [ , pushLocation] = useLocation();







  const evsubmit = (evt) => {
    evt.preventDefault();
    pushLocation(`/search/${keyword}/${rating}`);
  };



  function handleChange(evt) {
    update_keyword(evt.target.value)

  }

  const HandleChangeRating =(evt) =>{

  update_rating(evt.target.value)
  }

  return (
    <>
      <form onSubmit={evsubmit}>
        <button> Search </button>
        <input
          placeholder="search here"
          onChange={handleChange}
          type="text"
        ></input>
        <select  onChange={HandleChangeRating}value={rating}>
            <option disabled> Rating Type </option>
          {RATINGS.map((rating) => {
            return <option key={rating}> {rating} </option>;
          })}

        </select>
      </form>
      <h1> {times} </h1>
    </>
  );
        }


     //   export default  SearchForm

export default React.memo(SearchForm);

//use memo es para un valor podriamos usarlo dentro de la home  pero no en cualquier sitio si no  enla home

