import React, { useEffect, useState, useRef } from "react";
import { Link } from "wouter";
import getTrending from "../pages/getTrendingService";
import { key } from "../settings/settings";
import useNearScreen from "../hooks/isNearscreen";

 function TrendingSearches(){


    const [trends, setTrends] = useState([])


    useEffect(()=>{

        getTrending()
        .then(gifs => {

            setTrends(gifs)
        })


    }
    ,[])




  const listItems =   trends.map(singleGif =>{
        return(
            <li key={singleGif.id}>
            <Link to ={`/search/${singleGif.title}` }>

            <small > {singleGif.title} </small>

            </Link>
            </li>
        )
    })

    return (

        <div>

            <ul > {listItems } </ul>



        </div>


    );

}




export default function LazyTrending(){




const show = useNearScreen()

return( <div ref={ref}>


{show ? <TrendingSearches/> : null}

</div>);

};
