import { useReducer, } from "react";
import { useLocation } from "wouter";
import React from "react";

const RATINGS = ["g", "pg", "pg-13", "r"];


const reducer =(state, action)=>{
  if(action.type === 'update_keyword'){
    return{
      ...state,
      keyword:  action.payload,
      time: state.times+1
    }
  }else if(action.type === 'update_rating')
  {
    return{
      ...state,
      rating: action.payload
    }
  }


  return state
  }



 function SearchForm({initialKeyword = '', initialRating='g'}) {



  //const [keyword, setKeyword] = useState(decodeURIComponent(initialKeyword));


  //const [times, setTimes] = useState(0)




     const [state , dispatch ]    =    useReducer(reducer, {
      keyword: decodeURIComponent(initialKeyword),
      rating: initialRating,
      times: 0,
     })

     const [ , pushLocation] = useLocation();



     const {keyword, rating, times} = state



  const evsubmit = (evt) => {
    evt.preventDefault();
    pushLocation(`/search/${keyword}/${rating}`);
  };



  function handleChange(evt) {
    dispatch({          type : 'update_keyword',           payload:  evt.target.value    })
  }

  const HandleChangeRating =(evt) =>{

    dispatch(   { type : 'update_rating',       payload:  evt.target.value})
  }

  return (
    <>
      <form onSubmit={evsubmit}>
        <button> Search </button>
        <input
          placeholder="search here"
          onChange={handleChange}
          type="text"
        ></input>
        <select  onChange={HandleChangeRating}value={rating}>
            <option disabled> Rating Type </option>
          {RATINGS.map((rating) => {
            return <option key={rating}> {rating} </option>;
          })}

        </select>
      </form>
      <h1> {times} </h1>
    </>
  );
        }


export default React.memo(SearchForm);

