    const isFav = favs.some(favId => favId === id)

    console.log()

    const HandleFav = () =>{
    if(!isLogged) return navigate('/login')
        addfav({id})
    }

return (
<button onClick={HandleFav}>
<span role={'img'} aria-label='Fav Gif'>❤️</span>

</button>
)
}
