import { Link } from "wouter"
import getTrending from "../pages/getTrendingService"

import "./Trending.css"



function TrendingSearches(){

    const [trending, setTrends] = useState([])

    useEffect(()=>{
        getTrending()
        .then(gifs => {

            setTrends(gifs)
        })
    }
    ,[])


    const trends = trending.slice(0,20)


    function chooseColor (index){

        if(index  % 4 === 0   ){


            return "red"
        }
        if(index % 4 === 1)
        return "green"

    if(index % 4 === 2){
        return 'yellow'
    }
    if(index % 4 === 3){
        return ' orange'
    }
}


  const listItems =   trends.map((singleGif, index) =>{
        return(
            <li key={singleGif.title}  style={{'--bgTrending':`${chooseColor(index)}`}}>
            <Link to ={`/search/${singleGif.title}` }>

            <small > {singleGif.title} </small>

            </Link>
            </li>
        )
    })



    return (

        <div className="Trending">
            <ul > {listItems } </ul>

        </div>


    );

}

export default TrendingSearches;
