 if(isLoading) return( <h1> Loading </h1>)
 if(isError) return <Redirect to='/404' />
if(!gif) return null;

       return(
        <div>
          <h1 > {gif.title}</h1>
          <img src={gif.url} alt='-' />

        </div>
       )

}
