import { useState } from "react"
import { useLocation } from "wouter";
import useUser from "../../hooks/useUser";


export default function FavBar () {



    const { isLogged} = useUser();

    const [location ,navigate] = useLocation()




    const [navBar, setNavBar] = useState(location.split('/')[1])

    const handleClick = (route) => {


        if(route === 'gifs'){


            setNavBar('gifs')

            navigate(`/`)
        }

        if(route === 'favs'){



            setNavBar('favs')

            navigate(`/favs`)


        }

    }

    return(
        <div className="fav-selector">


        <div className={`active-section ${navBar ==='gifs' ? 'active':''}`} onClick={()=>{handleClick('gifs')}}>
          <h1>Gifs</h1>
            <div ></div>
        </div>


        {isLogged&&

        <div className={`active-section ${navBar === 'favs'? 'active':''}`} onClick={()=>{handleClick('favs')}}>
          <h1>Favs</h1>
          <div></div>
        </div>



        }


        </div>
    )
}
