import getTemplate from '../generators/TemplateGenerator';
import fs from 'fs';



const getConfig = function(){
		let settings = JSON.parse(getTemplate('init', 'config'));
        let userSettings = {};

        if(fs.existsSync('./.giffyconfig')){
            userSettings = JSON.parse(fs.readFileSync('./.giffyconfig', {encoding: 'utf-8'}));
        }
        return Object.assign(settings,userSettings);
}

export default getConfig;