#!/usr/bin/env node
import Builder from './generators/Builder.js';


const name = process.argv[3];

switch (process.argv[2]) {
    case 'init':
        let config = new Builder('init', 'config')
        console.log(config.build());
        break;
    case 'make:page':
        console.log(name);
        let page = new Builder(name, 'page');
        console.log(page.build());

        break;
    case 'make:action':
        let action = new Builder(name, 'action');
        console.log(action.build());

        break;
    case 'make:reducer':
        let reducer = new Builder(name, 'reducer');
        console.log(action.build());

        break;
    default:
        console.log('Command not found. \n Giffy version: 1.0');
}
